SET NAMES 'utf8mb4';
SET CHARACTER SET utf8mb4;
DROP DATABASE IF EXISTS brockdb ;
CREATE DATABASE brockdb;
USE brockdb;

DELIMITER ;;


CREATE TABLE Course(
    CrsID INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    CrsCode VARCHAR(255),
    CrsName VARCHAR(255),
    CrsDesc TEXT(65535),
    CrsDuration VARCHAR(255),
    CrsDays VARCHAR(255),
    CrsTiming VARCHAR(255),
    DeliveryMethod VARCHAR(255),
    PreReqs TEXT(25565),
    CrsInstructor TEXT(25565),
    CrsLocation VARCHAR(255)
);

CREATE TABLE PreRequisites(
    PrereqID INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    CrsCode VARCHAR(255) NOT NULL,
    PrereqCode VARCHAR(255) NOT NULL
);

CREATE TABLE Exam(
    ExamID INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    CrsCode VARCHAR(255) NOT NULL,
    CrsDuration VARCHAR(255) NOT NULL,
    CrsSection INT NOT NULL,
    ExamDate VARCHAR(255) NOT NULL,
    ExamStartTime VARCHAR(255) NOT NULL,
    ExamEndTime VARCHAR(255) NOT NULL,
    ExamLocation VARCHAR(255) NOT NULL
);

-- (CrsCode, CrsDuration, CrsSection, ExamDate, ExamStartTime, ExamEndTime, ExamLocation)

CREATE TABLE Department(
    DeptID INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    DeptName VARCHAR(255) NOT NULL,
    DeptPrefix VARCHAR(255) NOT NULL,
    DeptLink VARCHAR(255) NOT NULL,
    DeptChair VARCHAR(255) NOT NULL,
    ParentFac VARCHAR(255) NOT NULL
);

-- StaffDept, FirstName, LastName, Email, Email_Url, Title, StaffLocation
CREATE TABLE Staff(
    StaffID INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    StaffDept VARCHAR(255),
    FirstName VARCHAR(255),
    LastName VARCHAR(255),
    Email VARCHAR(255),
    Email_Url VARCHAR(255),
    Title VARCHAR(255),
    Extension VARCHAR(255),
    StaffLocation VARCHAR(255)
);

CREATE TABLE Faculty(
    FacID INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    FacDept VARCHAR(255),
    FirstName VARCHAR(255),
    LastName VARCHAR(255),
    Email VARCHAR(255),
    Email_Url VARCHAR(255),
    Title VARCHAR(255),
    Extension VARCHAR(255),
    FacLocation VARCHAR(255)
);

CREATE TABLE Program(
    PrgID INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    PrgName VARCHAR(255) NOT NULL,
    PrgDesc TEXT(25565) NOT NULL,
    PrgLink VARCHAR(255) NOT NULL,
    PrgReqs VARCHAR(255),
    PrgFac VARCHAR(255) NOT NULL
);

CREATE TABLE EventInfo(
    EventID INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    EventName VARCHAR(255) NOT NULL,
    EventLink VARCHAR(255) NOT NULL,
    EventDate VARCHAR(255) NOT NULL,
    EventDateLink VARCHAR(255) NOT NULL,
    EventDelivery VARCHAR(255) NOT NULL,
    EventDeliveryLink VARCHAR(255) NOT NULL
);
CREATE TABLE Club(
    ClubID INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    ClubName VARCHAR(255) NOT NULL,
    ClubContact VARCHAR(255),
    ClubDesc TEXT(25565)
);


